import { App as SendbirdApp } from "sendbird-uikit";
import "sendbird-uikit/dist/index.css";
import "@sendbird/chat";
import "@sendbird/chat/groupChannel";
import "@sendbird/chat/message";

export default function App({ channel }) {
  // Your markAsRead function implementation
  const markAsRead = () => {
    // Implement your logic to mark messages as read here
    console.log("Marking messages as read...");
  };

  useEffect(() => {
    if (channel?.unreadMessageCount) {
      markAsRead();
    }
  }, [markAsRead, channel]);

  return (
    <div className="App" style={{ height: 600 }}>
      <SendbirdApp
        appId={process.env.REACT_APP_SENDBIRD_APP_API_KEY}
        userId="john"
        theme="dark"
      />
    </div>
  );
}
