import React, { ChangeEventHandler } from 'react';

/**
 * The default value of `checked` should be null
 * to support both case of controlled and uncontrolled component
 * ref: https://github.com/aaronshaf/react-toggle#props
 */
const noop = (): void => { /* noop */ };
export const TOGGLE_DEFAULT_VALUE = {
  checked: null,
  defaultChecked: false,
  disabled: false,
  onChange: noop,
  onFocus: noop,
  onBlur: noop,
};

export interface ToggleContextInterface {
  checked?: boolean | null;
  defaultChecked?: boolean;
  disabled?: boolean;
  onChange?: ChangeEventHandler<HTMLInputElement>;
  onFocus?: ChangeEventHandler<HTMLInputElement>;
  onBlur?: ChangeEventHandler<HTMLInputElement>;
}

export const ToggleContext = React.createContext<ToggleContextInterface>(TOGGLE_DEFAULT_VALUE);

export function useToggleContext(): ToggleContextInterface {
  const context = React.useContext(ToggleContext);
  if (context === undefined) {
    throw new Error('@sendbird/uikit-react/ui/Toggle: useToggleContext must be used within a ToggleContainer.');
  }
  return context;
}
import React, { ChangeEvent, useCallback, useState } from 'react';

import {
  ToggleContext,
  ToggleContextInterface,
  TOGGLE_DEFAULT_VALUE as Dvalue,
} from './ToggleContext';

export interface ToggleContainerProps extends ToggleContextInterface {
  children?: React.ReactElement;
}

// Props Explanation https://github.com/aaronshaf/react-toggle#props
export function ToggleContainer({
  checked = Dvalue.checked, // null
  defaultChecked = Dvalue.defaultChecked,
  disabled = Dvalue.disabled,
  onChange = Dvalue.onChange,
  onFocus = Dvalue.onFocus,
  onBlur = Dvalue.onBlur,
  children,
}: ToggleContainerProps): React.ReactElement {
  const [isChecked, setChecked] = useState(defaultChecked || false);
  const handleChange = useCallback((e: ChangeEvent<HTMLInputElement>) => {
    if (disabled) {
      return;
    }
    if (checked === null) {
      setChecked(e.currentTarget.checked);
    }
    onChange(e);
  }, [onChange, checked]);

  return (
    <ToggleContext.Provider value={{
      checked: checked !== null ? checked : isChecked,
      disabled,
      onChange: handleChange,
      onFocus: (e) => {
        if (!disabled) {
          onFocus(e);
        }
      },
      onBlur: (e) => {
        if (!disabled) {
          onBlur(e);
        }
      },
    }}>
      {children}
    </ToggleContext.Provider>
  );
}
import useSendbirdStateContext from '../../hooks/useSendbirdStateContext';

export function filterNumber(input: string | number): Array<number> {
  if (typeof input !== 'string' && typeof input !== 'number') {
    try {
      const { config } = useSendbirdStateContext();
      const { logger } = config;
      logger.warning('@sendbird/uikit-react/ui/Toggle: TypeError - expected string or number.', input);
    } catch (_) { /* noop */ }
    return [];
  }
  if (typeof input === 'number') {
    return [input];
  }
  const regex = /(-?\d+)(\.\d+)?/g;
  const numbers = input.match(regex) || [];
  return numbers.map(parseFloat);
}
import React, { useState } from 'react';
import { useToggleContext } from './ToggleContext';
import { filterNumber } from './utils';

export interface ToggleUIProps {
  reversed?: boolean;
  width?: string;
  animationDuration?: string;
  style?: Record<string, string>;
  name?: string;
  id?: string;
  ariaLabel?: string;
  ariaLabelledby?: string;
}

export function ToggleUI(props: ToggleUIProps): React.ReactElement {
  const {
    reversed = false,
    width = '40px',
    animationDuration = '0.5s',
    style = {},
    name = '',
    id = '',
    ariaLabel = '',
    ariaLabelledby = '',
  } = props;
  const {
    checked,
    disabled,
    onChange,
    onFocus,
    onBlur,
  } = useToggleContext();

  // animation should not be activated in the initialization step
  const [animatedClassName, setAnimatedClassName] = useState('');

  const toggleWidth = filterNumber(width)?.[0];
  const toggleHeight = toggleWidth / 2;
  // The size of dot should be 60% of toggle height
  const dotSize = toggleHeight * 0.6;

  return (
    <label
      className={[
        'sendbird-input-toggle-button',
        animatedClassName,
        checked ? 'sendbird-input-toggle-button--checked' : 'sendbird-input-toggle-button--unchecked',
        disabled ? 'sendbird-input-toggle-button--disabled' : [],
        reversed ? 'sendbird-input-toggle-button--reversed' : [],
      ].flat().join(' ')}
      style={{
        width: `${toggleWidth}px`,
        height: `${toggleHeight}px`,
        borderRadius: `${dotSize}px`,
        ...style,
      }}
    >
      <div
        className={[
          'sendbird-input-toggle-button__inner-dot',
          checked
            ? 'sendbird-input-toggle-button__inner-dot--activate'
            : 'sendbird-input-toggle-button__inner-dot--inactivate',
        ].join(' ')}
        style={{
          width: `${dotSize}px`,
          height: `${dotSize}px`,
          animationDuration,
        }}
      />
      <input
        type="checkbox"
        aria-label={ariaLabel}
        aria-labelledby={ariaLabelledby}
        name={name}
        id={id}
        checked={checked}
        disabled={disabled}
        onChange={(e) => {
          onChange(e);
          setAnimatedClassName(e.currentTarget.checked ? 'sendbird-input-toggle-button--turned-on' : 'sendbird-input-toggle-button--turned-off');
        }}
        onFocus={onFocus}
        onBlur={onBlur}
      />
    </label>
  );
}
import React from 'react';
import './index.scss';

import { ToggleContainer, ToggleContainerProps } from './ToggleContainer';
import { useToggleContext } from './ToggleContext';
import { ToggleUI, ToggleUIProps } from './ToggleUI';

export interface ToggleProps extends ToggleContainerProps, ToggleUIProps {
  className?: string;
}
function Toggle(props: ToggleProps): React.ReactElement {
  const {
    // ToggleProvider
    checked,
    defaultChecked,
    disabled,
    onChange,
    onFocus,
    onBlur,
    // ToggleUI
    className,
    reversed,
    width,
    // height will be half of width
    animationDuration,
    style,
    name,
    id,
    ariaLabel,
    ariaLabelledby,
  } = props;
  return (
    <div className={`sendbird-ui-toggle ${className}`}>
      <ToggleContainer
        checked={checked}
        defaultChecked={defaultChecked}
        disabled={disabled}
        onChange={onChange}
        onFocus={onFocus}
        onBlur={onBlur}
        >
        <ToggleUI
          reversed={reversed}
          width={width}
          animationDuration={animationDuration}
          style={style}
          name={name}
          id={id}
          ariaLabel={ariaLabel}
          ariaLabelledby={ariaLabelledby}
        />
      </ToggleContainer>
    </div>
  );
}

export type { ToggleContainerProps, ToggleUIProps };
export { Toggle, ToggleContainer, ToggleUI, useToggleContext };
