import format from 'date-fns/format';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { BaseMessage, FileMessage, UserMessage } from '@sendbird/chat/message';
import { getOutgoingMessageState, OutgoingMessageStates } from '../../../utils/exports/getOutgoingMessageState';

export const getNicknamesMapFromMembers = (members = []): Map<string, string> => {
  const nicknamesMap = new Map();
  for (let memberIndex = 0; memberIndex < members.length; memberIndex += 1) {
    const { userId, nickname } = members[memberIndex];
    nicknamesMap.set(userId, nickname);
  }
  return nicknamesMap;
};

export const getParentMessageFrom = (message: UserMessage | FileMessage | null): UserMessage | FileMessage | BaseMessage | null => {
  if (!message) {
    return null;
  }
  if (isParentMessage(message)) {
    return message;
  }
  if (isThreadMessage(message)) {
    return message?.parentMessage || null;
  }
  return null;
};

export const isParentMessage = (message: UserMessage | FileMessage): boolean => {
  return (
    message?.parentMessage === null
    && typeof message?.parentMessageId === 'number'
    && !message?.parentMessageId
  );
};

export const isThreadMessage = (message: UserMessage | FileMessage): boolean => {
  return (
    message?.parentMessage !== null
    && typeof message?.parentMessageId === 'number'
    && message?.parentMessageId > 0
    && message?.threadInfo === null
  );
};

export const isAboutSame = (a: number, b: number, px: number): boolean => (Math.abs(a - b) <= px);

export const isEmpty = (val: unknown): boolean => (val === null || val === undefined);

// Some Ids return string and number inconsistently
// only use to comapre IDs
export function compareIds(a: number | string, b: number | string): boolean {
  if (isEmpty(a) || isEmpty(b)) {
    return false;
  }
  const aString = a.toString();
  const bString = b.toString();
  return aString === bString;
}

export const getMessageCreatedAt = (message: UserMessage | FileMessage): string => format(message.createdAt, 'p');
export const isReadMessage = (channel: GroupChannel, message: UserMessage | FileMessage): boolean => (
  getOutgoingMessageState(channel, message) === OutgoingMessageStates.READ
);
export const isSameGroup = (
  message: UserMessage | FileMessage,
  comparingMessage: UserMessage | FileMessage,
  currentChannel: GroupChannel,
): boolean => {
  if (!(message
    && comparingMessage
    && message.messageType
    && message.messageType !== 'admin'
    && comparingMessage.messageType
    && comparingMessage?.messageType !== 'admin'
    && message?.sender
    && comparingMessage?.sender
    && message?.createdAt
    && comparingMessage?.createdAt
    && message?.sender?.userId
    && comparingMessage?.sender?.userId
  )) {
    return false;
  }
  return (
    message?.sendingStatus === comparingMessage?.sendingStatus
    && message?.sender?.userId === comparingMessage?.sender?.userId
    && getMessageCreatedAt(message) === getMessageCreatedAt(comparingMessage)
    && isReadMessage(currentChannel, message) === isReadMessage(currentChannel, comparingMessage)
  );
};

export const compareMessagesForGrouping = (
  prevMessage: UserMessage | FileMessage,
  currMessage: UserMessage | FileMessage,
  nextMessage: UserMessage | FileMessage,
  currentChannel: GroupChannel,
  replyType: string,
): [boolean, boolean] => {
  if (replyType === 'THREAD' && currMessage?.threadInfo) {
    return [false, false];
  }
  const sendingStatus = currMessage?.sendingStatus || '';
  const isAcceptable = sendingStatus !== 'pending' && sendingStatus !== 'failed';
  return [
    isSameGroup(prevMessage, currMessage, currentChannel) && isAcceptable,
    isSameGroup(currMessage, nextMessage, currentChannel) && isAcceptable,
  ];
};

export const scrollIntoLast = (intialTry = 0): void => {
  const MAX_TRIES = 10;
  const currentTry = intialTry;
  if (currentTry > MAX_TRIES) {
    return;
  }
  try {
    const scrollDOM = document.querySelector('.sendbird-thread-ui--scroll');
    // eslint-disable-next-line no-multi-assign
    scrollDOM.scrollTop = scrollDOM.scrollHeight;
  } catch (error) {
    setTimeout(() => {
      scrollIntoLast(currentTry + 1);
    }, 500 * currentTry);
  }
};
export const PREV_THREADS_FETCH_SIZE = 30;
export const NEXT_THREADS_FETCH_SIZE = 30;
export enum ThreadContextActionTypes {
  // initialize
  INIT_USER_ID = 'INIT_USER_ID',
  // channel
  GET_CHANNEL_START = 'GET_CHANNEL_START',
  GET_CHANNEL_SUCCESS = 'GET_CHANNEL_SUCCESS',
  GET_CHANNEL_FAILURE = 'GET_CHANNEL_FAILURE',
  // emojis
  SET_EMOJI_CONTAINER = 'SET_EMOJI_CONTAINER',
  // parent message
  GET_PARENT_MESSAGE_START = 'GET_PARENT_MESSAGE_START',
  GET_PARENT_MESSAGE_SUCCESS = 'GET_PARENT_MESSAGE_SUCCESS',
  GET_PARENT_MESSAGE_FAILURE = 'GET_PARENT_MESSAGE_FAILURE',
  // fetch threads
  INITIALIZE_THREAD_LIST_START = 'INITIALIZE_THREAD_LIST_START',
  INITIALIZE_THREAD_LIST_SUCCESS = 'INITIALIZE_THREAD_LIST_SUCCESS',
  INITIALIZE_THREAD_LIST_FAILURE = 'INITIALIZE_THREAD_LIST_FAILURE',
  GET_PREV_MESSAGES_START = 'GET_PREV_MESSAGES_START',
  GET_PREV_MESSAGES_SUCESS = 'GET_PREV_MESSAGES_SUCESS',
  GET_PREV_MESSAGES_FAILURE = 'GET_PREV_MESSAGES_FAILURE',
  GET_NEXT_MESSAGES_START = 'GET_NEXT_MESSAGES_START',
  GET_NEXT_MESSAGES_SUCESS = 'GET_NEXT_MESSAGES_SUCESS',
  GET_NEXT_MESSAGES_FAILURE = 'GET_NEXT_MESSAGES_FAILURE',
  // handle messages
  SEND_MESSAGE_START = 'SEND_MESSAGE_START',
  SEND_MESSAGE_SUCESS = 'SEND_MESSAGE_SUCESS',
  SEND_MESSAGE_FAILURE = 'SEND_MESSAGE_FAILURE',
  RESEND_MESSAGE_START = 'RESEND_MESSAGE_START',
  ON_MESSAGE_DELETED_BY_REQ_ID = 'ON_MESSAGE_DELETED_BY_REQ_ID',
  // event handlers - message status change
  ON_MESSAGE_RECEIVED = 'ON_MESSAGE_RECEIVED',
  ON_MESSAGE_UPDATED = 'ON_MESSAGE_UPDATED',
  ON_MESSAGE_DELETED = 'ON_MESSAGE_DELETED',
  ON_REACTION_UPDATED = 'ON_REACTION_UPDATED',
  // event handlers - user status change
  ON_USER_MUTED = 'ON_USER_MUTED',
  ON_USER_UNMUTED = 'ON_USER_UNMUTED',
  ON_USER_BANNED = 'ON_USER_BANNED',
  ON_USER_UNBANNED = 'ON_USER_UNBANNED',
  ON_USER_LEFT = 'ON_USER_LEFT',
  // event handler - channel status change
  ON_CHANNEL_FROZEN = 'ON_CHANNEL_FROZEN',
  ON_CHANNEL_UNFROZEN = 'ON_CHANNEL_UNFROZEN',
  ON_OPERATOR_UPDATED = 'ON_OPERATOR_UPDATED',
}
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, ReactionEvent, UserMessage } from '@sendbird/chat/message';
import { NEXT_THREADS_FETCH_SIZE, PREV_THREADS_FETCH_SIZE } from '../../consts';
import { ChannelStateTypes, ParentMessageStateTypes, ThreadListStateTypes } from '../../types';
import { compareIds } from '../utils';
import { ThreadContextActionTypes as actionTypes } from './actionTypes';
import { ThreadContextInitialState } from './initialState';

interface ActionInterface {
  type: actionTypes;
  payload?: any;
}

export default function reducer(
  state: ThreadContextInitialState,
  action: ActionInterface,
): ThreadContextInitialState {
  switch (action.type) {
    // initialize
    case actionTypes.INIT_USER_ID: {
      return {
        ...state,
        currentUserId: action.payload,
      };
    }
    case actionTypes.GET_CHANNEL_START: {
      return {
        ...state,
        channelState: ChannelStateTypes.LOADING,
        currentChannel: null,
      };
    }
    case actionTypes.GET_CHANNEL_SUCCESS: {
      const groupChannel = action.payload.groupChannel as GroupChannel;
      return {
        ...state,
        channelState: ChannelStateTypes.INITIALIZED,
        currentChannel: groupChannel,
        // only support in normal group channel
        isMuted: groupChannel?.members?.find((member) => member?.userId === state.currentUserId)?.isMuted || false,
        isChannelFrozen: groupChannel?.isFrozen || false,
      };
    }
    case actionTypes.GET_CHANNEL_FAILURE: {
      return {
        ...state,
        channelState: ChannelStateTypes.INVALID,
        currentChannel: null,
      };
    }
    case actionTypes.SET_EMOJI_CONTAINER: {
      const { emojiContainer } = action.payload;
      return {
        ...state,
        emojiContainer: emojiContainer,
      };
    }
    case actionTypes.GET_PARENT_MESSAGE_START: {
      return {
        ...state,
        parentMessageState: ParentMessageStateTypes.LOADING,
        parentMessage: null,
      };
    }
    case actionTypes.GET_PARENT_MESSAGE_SUCCESS: {
      return {
        ...state,
        parentMessageState: ParentMessageStateTypes.INITIALIZED,
        parentMessage: action.payload.parentMessage,
      };
    }
    case actionTypes.GET_PARENT_MESSAGE_FAILURE: {
      return {
        ...state,
        parentMessageState: ParentMessageStateTypes.INVALID,
        parentMessage: null,
      };
    }
    // fetch threads
    case actionTypes.INITIALIZE_THREAD_LIST_START: {
      return {
        ...state,
        threadListState: ThreadListStateTypes.LOADING,
        allThreadMessages: [],
      };
    }
    case actionTypes.INITIALIZE_THREAD_LIST_SUCCESS: {
      const { parentMessage, anchorMessage, threadedMessages } = action.payload;
      const anchorMessageCreatedAt = (!anchorMessage?.messageId) ? parentMessage?.createdAt : anchorMessage?.createdAt;
      const anchorIndex = threadedMessages.findIndex((message) => message?.createdAt > anchorMessageCreatedAt);
      const prevThreadMessages = anchorIndex > -1 ? threadedMessages.slice(0, anchorIndex) : threadedMessages;
      const anchorThreadMessage = anchorMessage?.messageId ? [anchorMessage] : [];
      const nextThreadMessages = anchorIndex > -1 ? threadedMessages.slice(anchorIndex) : [];
      return {
        ...state,
        threadListState: ThreadListStateTypes.INITIALIZED,
        hasMorePrev: anchorIndex === -1 || anchorIndex === PREV_THREADS_FETCH_SIZE,
        hasMoreNext: threadedMessages.length - anchorIndex === NEXT_THREADS_FETCH_SIZE,
        allThreadMessages: [prevThreadMessages, anchorThreadMessage, nextThreadMessages].flat(),
      };
    }
    case actionTypes.INITIALIZE_THREAD_LIST_FAILURE: {
      return {
        ...state,
        threadListState: ThreadListStateTypes.INVALID,
        allThreadMessages: [],
      };
    }
    case actionTypes.GET_NEXT_MESSAGES_START: {
      return {
        ...state,
      };
    }
    case actionTypes.GET_NEXT_MESSAGES_SUCESS: {
      const { threadedMessages } = action.payload;
      return {
        ...state,
        hasMoreNext: threadedMessages.length === NEXT_THREADS_FETCH_SIZE,
        allThreadMessages: [...state.allThreadMessages, ...threadedMessages],
      };
    }
    case actionTypes.GET_NEXT_MESSAGES_FAILURE: {
      return {
        ...state,
        hasMoreNext: false,
      };
    }
    case actionTypes.GET_PREV_MESSAGES_START: {
      return {
        ...state,
      };
    }
    case actionTypes.GET_PREV_MESSAGES_SUCESS: {
      const { threadedMessages } = action.payload;
      return {
        ...state,
        hasMorePrev: threadedMessages.length === PREV_THREADS_FETCH_SIZE,
        allThreadMessages: [...threadedMessages, ...state.allThreadMessages],
      };
    }
    case actionTypes.GET_PREV_MESSAGES_FAILURE: {
      return {
        ...state,
        hasMorePrev: false,
      };
    }
    // event handlers - message status change
    case actionTypes.ON_MESSAGE_RECEIVED: {
      const { channel, message }: { channel: GroupChannel, message: UserMessage | FileMessage } = action.payload;

      if (
        state.currentChannel?.url !== channel?.url
        || state.hasMoreNext
        || message?.parentMessage?.messageId !== state?.parentMessage?.messageId
      ) {
        return state;
      }
      const isAlreadyReceived = state.allThreadMessages.findIndex((m) => (
        m.messageId === message.messageId
      )) > -1;
      return {
        ...state,
        parentMessage: state.parentMessage?.messageId === message?.messageId ? message : state.parentMessage,
        allThreadMessages: isAlreadyReceived
          ? state.allThreadMessages.map((m) => (
            m.messageId === message.messageId ? message : m
          ))
          : [
            ...state.allThreadMessages.filter((m) => (m as UserMessage | FileMessage)?.reqId !== message?.reqId),
            message,
          ],
      };
    }
    case actionTypes.ON_MESSAGE_UPDATED: {
      const { channel, message } = action.payload;
      if (state.currentChannel?.url !== channel?.url) {
        return state;
      }
      return {
        ...state,
        parentMessage: state.parentMessage?.messageId === message?.messageId
          ? message
          : state.parentMessage,
        allThreadMessages: state.allThreadMessages?.map((msg) => (
          (msg?.messageId === message?.messageId) ? message : msg
        )),
      };
    }
    case actionTypes.ON_MESSAGE_DELETED: {
      const { channel, messageId } = action.payload;
      if (state.currentChannel?.url !== channel?.url) {
        return state;
      }
      if (state?.parentMessage?.messageId === messageId) {
        return {
          ...state,
          parentMessage: null,
          parentMessageState: ParentMessageStateTypes.NIL,
          allThreadMessages: [],
        };
      }
      return {
        ...state,
        allThreadMessages: state.allThreadMessages?.filter((msg) => (
          msg?.messageId !== messageId
        )),
      };
    }
    case actionTypes.ON_MESSAGE_DELETED_BY_REQ_ID: {
      return {
        ...state,
        allThreadMessages: state.allThreadMessages.filter((m) => (
          !compareIds((m as UserMessage | FileMessage).reqId, action.payload)
        )),
      };
    }
    case actionTypes.ON_REACTION_UPDATED: {
      const reactionEvent = action.payload?.reactionEvent as ReactionEvent;
      if (state?.parentMessage?.messageId === reactionEvent?.messageId) {
        state.parentMessage?.applyReactionEvent?.(reactionEvent);
      }
      return {
        ...state,
        allThreadMessages: state.allThreadMessages.map((m) => {
          if (reactionEvent?.messageId === m?.messageId) {
            m?.applyReactionEvent?.(reactionEvent);
            return m;
          }
          return m;
        }),
      };
    }
    // event handlers - user status change
    case actionTypes.ON_USER_MUTED: {
      const { channel, user } = action.payload;
      if (state.currentChannel?.url !== channel?.url || state.currentUserId !== user?.userId) {
        return state;
      }
      return {
        ...state,
        isMuted: true,
      };
    }
    case actionTypes.ON_USER_UNMUTED: {
      const { channel, user } = action.payload;
      if (state.currentChannel?.url !== channel?.url || state.currentUserId !== user?.userId) {
        return state;
      }
      return {
        ...state,
        isMuted: false,
      };
    }
    case actionTypes.ON_USER_BANNED: {
      return {
        ...state,
        channelState: ChannelStateTypes.NIL,
        threadListState: ThreadListStateTypes.NIL,
        parentMessageState: ParentMessageStateTypes.NIL,
        currentChannel: null,
        parentMessage: null,
        allThreadMessages: [],
        hasMorePrev: false,
        hasMoreNext: false,
      };
    }
    case actionTypes.ON_USER_UNBANNED: {
      return {
        ...state,
      };
    }
    case actionTypes.ON_USER_LEFT: {
      return {
        ...state,
        channelState: ChannelStateTypes.NIL,
        threadListState: ThreadListStateTypes.NIL,
        parentMessageState: ParentMessageStateTypes.NIL,
        currentChannel: null,
        parentMessage: null,
        allThreadMessages: [],
        hasMorePrev: false,
        hasMoreNext: false,
      };
    }
    // event handler - channel status change
    case actionTypes.ON_CHANNEL_FROZEN: {
      return {
        ...state,
        isChannelFrozen: true,
      };
    }
    case actionTypes.ON_CHANNEL_UNFROZEN: {
      return {
        ...state,
        isChannelFrozen: false,
      };
    }
    case actionTypes.ON_OPERATOR_UPDATED: {
      const { channel } = action.payload;
      if (channel?.url === state.currentChannel?.url) {
        return {
          ...state,
          currentChannel: channel,
        };
      }
      return state;
    }
    // message
    case actionTypes.SEND_MESSAGE_START: {
      const { message } = action.payload;
      return {
        ...state,
        allThreadMessages: [
          ...state.allThreadMessages,
          message,
        ],
      };
    }
    case actionTypes.SEND_MESSAGE_SUCESS: {
      const { message } = action.payload;
      const filteredThreadMessages = state.allThreadMessages.filter((m) => (
        !compareIds((m as UserMessage)?.reqId, message?.reqId)
      ));
      return {
        ...state,
        allThreadMessages: [
          ...filteredThreadMessages,
          message,
        ],
      };
    }
    case actionTypes.SEND_MESSAGE_FAILURE: {
      const { message } = action.payload;
      return {
        ...state,
        allThreadMessages: state.allThreadMessages.map((m) => (
          compareIds((m as UserMessage)?.reqId, message?.reqId)
            ? message
            : m
        )),
      };
    }
    case actionTypes.RESEND_MESSAGE_START: {
      return {
        ...state,
      };
    }
    default: {
      return state;
    }
  }
}
