    if (sdkInit && parentMessage?.getThreadedMessagesByTimestamp) {
      threadDispatcher({
        type: ThreadContextActionTypes.INITIALIZE_THREAD_LIST_START,
        payload: null,
      });
      const timeStamp = anchorMessage?.createdAt || 0;
      const params = {
