        .then(({ parentMessage, threadedMessages }) => {
          logger.info('Thread | useGetThreadList: Initialize thread list succeeded.', { parentMessage, threadedMessages });
          threadDispatcher({
            type: ThreadContextActionTypes.INITIALIZE_THREAD_LIST_SUCCESS,
            payload: {
              parentMessage,
              anchorMessage,
              threadedMessages,
