import { useEffect } from 'react';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, UserMessage } from '@sendbird/chat/message';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import topics from '../../../../lib/pubSub/topics';
import { scrollIntoLast } from '../utils';
import { ThreadContextActionTypes } from '../dux/actionTypes';

interface DynamicProps {
  sdkInit: boolean;
  currentChannel: GroupChannel;
  parentMessage: UserMessage | FileMessage
}
interface StaticProps {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useHandlePubsubEvents({
  sdkInit,
  currentChannel,
  parentMessage,
}: DynamicProps, {
  pubSub,
  threadDispatcher,
}: StaticProps): void {
  useEffect(() => {
    const pubSubHandler = (): Map<any, any> => {
      const subscriber = new Map();
      if (!pubSub || !pubSub.subscribe) {
        return subscriber;
      }
      subscriber.set(topics.SEND_USER_MESSAGE, pubSub.subscribe(topics.SEND_USER_MESSAGE, (props) => {
        const { channel, message } = props;
        if (currentChannel?.url === channel?.url
          && message?.parentMessageId === parentMessage?.messageId
        ) {
          threadDispatcher({
            type: ThreadContextActionTypes.SEND_MESSAGE_SUCESS,
            payload: { message },
          });
        }
        scrollIntoLast?.();
      }));
      subscriber.set(topics.SEND_FILE_MESSAGE, pubSub.subscribe(topics.SEND_FILE_MESSAGE, (props) => {
        const { channel, message } = props;
        if (currentChannel?.url === channel?.url) {
          threadDispatcher({
            type: ThreadContextActionTypes.SEND_MESSAGE_SUCESS,
            payload: { message },
          });
        }
        scrollIntoLast?.();
      }));
      subscriber.set(topics.UPDATE_USER_MESSAGE, pubSub.subscribe(topics.UPDATE_USER_MESSAGE, (msg) => {
        const { channel, message } = msg;
        if (currentChannel?.url === channel?.url) {
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_UPDATED,
            payload: { channel, message },
          });
        }
      }));
      subscriber.set(topics.DELETE_MESSAGE, pubSub.subscribe(topics.DELETE_MESSAGE, (msg) => {
        const { channel, messageId } = msg;
        if (currentChannel?.url === channel?.url) {
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_DELETED,
            payload: { messageId },
          });
        }
      }));
    };
    const subscriber = pubSubHandler();
    return () => {
      subscriber?.forEach((s) => {
        try {
          s?.remove();
        } catch {
          //
        }
      });
    };
  }, [sdkInit, currentChannel]);
}
import { GroupChannel, GroupChannelHandler, SendbirdGroupChat } from '@sendbird/chat/groupChannel';
import { useEffect } from 'react';
import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import uuidv4 from '../../../../utils/uuid';
import { ThreadContextActionTypes } from '../dux/actionTypes';

interface DynamicProps {
  sdk: SendbirdGroupChat;
  currentChannel: GroupChannel;
}
interface StaticProps {
  logger: Logger;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useHandleChannelEvents({
  sdk,
  currentChannel,
}: DynamicProps, {
  logger,
  threadDispatcher,
}: StaticProps): void {
  useEffect(() => {
    const handlerId = uuidv4();
    // validation check
    if (sdk?.groupChannel?.addGroupChannelHandler
      && currentChannel
    ) {
      const channelHandlerParams: GroupChannelHandler = {
        // message status change
        onMessageReceived(channel, message) {
          logger.info('Thread | useHandleChannelEvents: onMessageReceived', { channel, message });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_RECEIVED,
            payload: { channel, message },
          });
        },
        onMessageUpdated(channel, message) {
          logger.info('Thread | useHandleChannelEvents: onMessageUpdated', { channel, message });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_UPDATED,
            payload: { channel, message },
          });
        },
        onMessageDeleted(channel, messageId) {
          logger.info('Thread | useHandleChannelEvents: onMessageDeleted', { channel, messageId });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_DELETED,
            payload: { channel, messageId },
          });
        },
        onReactionUpdated(channel, reactionEvent) {
          logger.info('Thread | useHandleChannelEvents: onReactionUpdated', { channel, reactionEvent });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_REACTION_UPDATED,
            payload: { channel, reactionEvent },
          });
        },
        // user status change
        onUserMuted(channel, user) {
          logger.info('Thread | useHandleChannelEvents: onUserMuted', { channel, user });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_USER_MUTED,
            payload: { channel, user },
          });
        },
        onUserUnmuted(channel, user) {
          logger.info('Thread | useHandleChannelEvents: onUserUnmuted', { channel, user });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_USER_UNMUTED,
            payload: { channel, user },
          });
        },
        onUserBanned(channel, user) {
          logger.info('Thread | useHandleChannelEvents: onUserBanned', { channel, user });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_USER_BANNED,
            payload: { channel, user },
          });
        },
        onUserUnbanned(channel, user) {
          logger.info('Thread | useHandleChannelEvents: onUserUnbanned', { channel, user });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_USER_UNBANNED,
            payload: { channel, user },
          });
        },
        onUserLeft(channel, user) {
          logger.info('Thread | useHandleChannelEvents: onUserLeft', { channel, user });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_USER_LEFT,
            payload: { channel, user },
          });
        },
        // channel status change
        onChannelFrozen(channel) {
          logger.info('Thread | useHandleChannelEvents: onChannelFrozen', { channel });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_CHANNEL_FROZEN,
            payload: { channel },
          });
        },
        onChannelUnfrozen(channel) {
          logger.info('Thread | useHandleChannelEvents: onChannelUnfrozen', { channel });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_CHANNEL_UNFROZEN,
            payload: { channel },
          });
        },
        onOperatorUpdated(channel, users) {
          logger.info('Thread | useHandleChannelEvents: onOperatorUpdated', { channel, users });
          threadDispatcher({
            type: ThreadContextActionTypes.ON_OPERATOR_UPDATED,
            payload: { channel, users },
          });
        },
      };
      const channelHandler = new GroupChannelHandler(channelHandlerParams);
      sdk.groupChannel.addGroupChannelHandler?.(handlerId, channelHandler);
      logger.info('Thread | useHandleChannelEvents: Added channelHandler in Thread', { handlerId, channelHandler });
    }
    return () => {
      // validation check
      if (handlerId && sdk?.groupChannel?.removeGroupChannelHandler) {
        sdk.groupChannel.removeGroupChannelHandler?.(handlerId);
        logger.info('Thread | useHandleChannelEvents: Removed channelHandler in Thread.', handlerId);
      }
    };
  }, [
    sdk?.groupChannel,
    currentChannel,
  ]);
}
import { useCallback } from 'react';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, FileMessageCreateParams } from '@sendbird/chat/message';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';
import topics from '../../../../lib/pubSub/topics';
import { scrollIntoLast } from '../utils';

interface DynamicProps {
  currentChannel: GroupChannel;
}
interface StaticProps {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}

interface LocalFileMessage extends FileMessage {
  localUrl: string;
  file: File;
}

export default function useSendFileMessageCallback({
  currentChannel,
}: DynamicProps, {
  logger,
  pubSub,
  threadDispatcher,
}: StaticProps): (file, quoteMessage) => void {
  const sendMessage = useCallback((file, quoteMessage) => {
    const createParamsDefault = () => {
      const params = {} as FileMessageCreateParams;
      params.file = file;
      if (quoteMessage) {
        params.isReplyToChannel = true;
        params.parentMessageId = quoteMessage.messageId;
      }
      return params;
    };
    const params = createParamsDefault();
    logger.info('Thread | useSendFileMessageCallback: Sending file message start.', params);

    currentChannel?.sendFileMessage(params)
      .onPending((pendingMessage) => {
        threadDispatcher({
          type: ThreadContextActionTypes.SEND_MESSAGE_START,
          payload: {
            /* pubSub is used instead of messagesDispatcher
            to avoid redundantly calling `messageActionTypes.SEND_MESSAGEGE_START` */
            message: {
              ...pendingMessage,
              url: URL.createObjectURL(file),
              // pending thumbnail message seems to be failed
              requestState: 'pending',
            },
          },
        });
        setTimeout(() => scrollIntoLast(), 1000);
      })
      .onFailed((error, message) => {
        (message as LocalFileMessage).localUrl = URL.createObjectURL(file);
        (message as LocalFileMessage).file = file;
        logger.info('Thread | useSendFileMessageCallback: Sending file message failed.', { message, error });
        threadDispatcher({
          type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
          payload: { message, error },
        });
      })
      .onSucceeded((message) => {
        logger.info('Thread | useSendFileMessageCallback: Sending file message succeeded.', message);
        pubSub.publish(topics.SEND_FILE_MESSAGE, {
          channel: currentChannel,
          message: message,
        });
      });
  }, [currentChannel]);
  return sendMessage;
}
import { useCallback } from 'react';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { UserMessage, UserMessageUpdateParams } from '@sendbird/chat/message';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';

import topics from '../../../../lib/pubSub/topics';

interface DynamicProps {
  currentChannel: GroupChannel;
  isMentionEnabled?: boolean;
}
interface StaticProps {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useUpdateMessageCallback({
  currentChannel,
  isMentionEnabled,
}: DynamicProps, {
  logger,
  pubSub,
  threadDispatcher,
}: StaticProps): (props) => void {
  return useCallback((props) => {
    const {
      messageId,
      message,
      mentionedUsers,
      mentionTemplate,
    } = props;
    const createParamsDefault = () => {
      const params = {} as UserMessageUpdateParams;
      params.message = message;
      if (isMentionEnabled && mentionedUsers?.length > 0) {
        params.mentionedUsers = mentionedUsers;
      }
      if (isMentionEnabled && mentionTemplate) {
        params.mentionedMessageTemplate = mentionTemplate;
      } else {
        params.mentionedMessageTemplate = message;
      }
      return params;
    };

    const params = createParamsDefault();
    logger.info('Thread | useUpdateMessageCallback: Message update start.', params);

    currentChannel?.updateUserMessage?.(messageId, params)
      .then((message: UserMessage) => {
        logger.info('Thread | useUpdateMessageCallback: Message update succeeded.', message);
        threadDispatcher({
          type: ThreadContextActionTypes.ON_MESSAGE_UPDATED,
          payload: {
            channel: currentChannel,
            message: message,
          },
        });
        pubSub.publish(
          topics.UPDATE_USER_MESSAGE,
          {
            fromSelector: true,
            channel: currentChannel,
            message: message,
          },
        );
      });
  }, [currentChannel, isMentionEnabled]);
}
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, UserMessage } from '@sendbird/chat/message';
import { useCallback } from 'react';
import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';

interface DynamicProps {
  currentChannel: GroupChannel;
  threadDispatcher: CustomUseReducerDispatcher;
}
interface StaticProps {
  logger: Logger;
}

export default function useDeleteMessageCallback({
  currentChannel,
  threadDispatcher,
}: DynamicProps, {
  logger,
}: StaticProps): (message: UserMessage | FileMessage) => Promise<UserMessage | FileMessage> {
  return useCallback((message: UserMessage | FileMessage): Promise<UserMessage | FileMessage> => {
    logger.info('Thread | useDeleteMessageCallback: Deleting message.', message);
    const { sendingStatus } = message;
    return new Promise((resolve, reject) => {
      logger.info('Thread | useDeleteMessageCallback: Deleting message requestState:', sendingStatus);
      // Message is only on local
      if (sendingStatus === 'failed' || sendingStatus === 'pending') {
        logger.info('Thread | useDeleteMessageCallback: Deleted message from local:', message);
        threadDispatcher({
          type: ThreadContextActionTypes.ON_MESSAGE_DELETED_BY_REQ_ID,
          payload: message.reqId,
        });
        resolve(message);
      }

      logger.info('Thread | useDeleteMessageCallback: Deleting message from remote:', sendingStatus);
      currentChannel?.deleteMessage?.(message)
        .then(() => {
          logger.info('Thread | useDeleteMessageCallback: Deleting message success!', message);
          threadDispatcher({
            type: ThreadContextActionTypes.ON_MESSAGE_DELETED,
            payload: { message, channel: currentChannel },
          });
          resolve(message);
        })
        .catch((err) => {
          logger.warning('Thread | useDeleteMessageCallback: Deleting message failed!', err);
          reject(err);
        });
    });
  }, [currentChannel]);
}
import { useCallback } from 'react';
import { BaseMessage, FileMessage, ThreadedMessageListParams, UserMessage } from '@sendbird/chat/message';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { PREV_THREADS_FETCH_SIZE } from '../../consts';
import { ThreadListStateTypes } from '../../types';
import { ThreadContextActionTypes } from '../dux/actionTypes';

interface DynamicProps {
  hasMorePrev: boolean;
  parentMessage: UserMessage | FileMessage;
  threadListState: ThreadListStateTypes;
  oldestMessageTimeStamp: number;
  isReactionEnabled?: boolean;
}
interface StaticProps {
  logger: Logger;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useGetPrevThreadsCallback({
  hasMorePrev,
  parentMessage,
  threadListState,
  oldestMessageTimeStamp,
  isReactionEnabled,
}: DynamicProps, {
  logger,
  threadDispatcher,
}: StaticProps): (callback?: (messages?: Array<BaseMessage>) => void) => void {
  return useCallback((callback) => {
    // validation check
    if (threadListState === ThreadListStateTypes.INITIALIZED
      && parentMessage?.getThreadedMessagesByTimestamp
      && oldestMessageTimeStamp !== 0
    ) {
      threadDispatcher({
        type: ThreadContextActionTypes.GET_PREV_MESSAGES_START,
        payload: null,
      });
      parentMessage.getThreadedMessagesByTimestamp?.(
        oldestMessageTimeStamp,
        {
          prevResultSize: PREV_THREADS_FETCH_SIZE,
          nextResultSize: 0,
          includeReactions: isReactionEnabled,
          includeMetaArray: true,
        } as ThreadedMessageListParams,
      )
        .then(({ parentMessage, threadedMessages }) => {
          logger.info('Thread | useGetPrevThreadsCallback: Fetch prev threads succeeded.', { parentMessage, threadedMessages });
          threadDispatcher({
            type: ThreadContextActionTypes.GET_PREV_MESSAGES_SUCESS,
            payload: { parentMessage, threadedMessages },
          });
          callback(threadedMessages);
        })
        .catch((error) => {
          logger.info('Thread | useGetPrevThreadsCallback: Fetch prev threads failed.', error);
          threadDispatcher({
            type: ThreadContextActionTypes.GET_PREV_MESSAGES_FAILURE,
            payload: error,
          });
        });
    }
  }, [
    hasMorePrev,
    parentMessage,
    threadListState,
    oldestMessageTimeStamp,
  ]);
}
import { BaseMessage, FileMessage, ThreadedMessageListParams, UserMessage } from '@sendbird/chat/message';
import { useCallback } from 'react';
import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { NEXT_THREADS_FETCH_SIZE } from '../../consts';
import { ThreadListStateTypes } from '../../types';
import { ThreadContextActionTypes } from '../dux/actionTypes';

interface DynamicProps {
  hasMoreNext: boolean;
  parentMessage: UserMessage | FileMessage;
  threadListState: ThreadListStateTypes;
  latestMessageTimeStamp: number;
  isReactionEnabled?: boolean;
}
interface StaticProps {
  logger: Logger;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useGetNextThreadsCallback({
  hasMoreNext,
  parentMessage,
  threadListState,
  latestMessageTimeStamp,
  isReactionEnabled,
}: DynamicProps, {
  logger,
  threadDispatcher,
}: StaticProps): (callback: (messages?: Array<BaseMessage>) => void) => void {
  return useCallback((callback) => {
    // validation check
    if (threadListState === ThreadListStateTypes.INITIALIZED
      && parentMessage?.getThreadedMessagesByTimestamp
      && latestMessageTimeStamp !== 0
    ) {
      threadDispatcher({
        type: ThreadContextActionTypes.GET_NEXT_MESSAGES_START,
        payload: null,
      });
      parentMessage.getThreadedMessagesByTimestamp?.(
        latestMessageTimeStamp,
        {
          prevResultSize: 0,
          nextResultSize: NEXT_THREADS_FETCH_SIZE,
          includeReactions: isReactionEnabled,
          includeMetaArray: true,
        } as ThreadedMessageListParams,
      )
        .then(({ parentMessage, threadedMessages }) => {
          logger.info('Thread | useGetNextThreadsCallback: Fetch next threads succeeded.', { parentMessage, threadedMessages });
          threadDispatcher({
            type: ThreadContextActionTypes.GET_NEXT_MESSAGES_SUCESS,
            payload: { parentMessage, threadedMessages },
          });
          callback(threadedMessages);
        })
        .catch((error) => {
          logger.info('Thread | useGetNextThreadsCallback: Fetch next threads failed.', error);
          threadDispatcher({
            type: ThreadContextActionTypes.GET_NEXT_MESSAGES_FAILURE,
            payload: error,
          });
        });
    }
  }, [
    hasMoreNext,
    parentMessage,
    threadListState,
    latestMessageTimeStamp,
  ]);
}
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { useCallback } from 'react';
import { Logger } from '../../../../lib/SendbirdState';

interface DynamicProps {
  currentChannel: GroupChannel;
}
interface StaticProps {
  logger: Logger;
}

export default function useToggleReactionCallback({
  currentChannel,
}: DynamicProps, {
  logger,
}: StaticProps): (message, key, isReacted) => void {
  return useCallback((message, key, isReacted) => {
    if (isReacted) {
      currentChannel?.deleteReaction?.(message, key)
        .then((res) => {
          logger.info('Thread | useToggleReactionsCallback: Delete reaction succeeded.', res);
        })
        .catch((err) => {
          logger.warning('Thread | useToggleReactionsCallback: Delete reaction failed.', err);
        });
      return;
    }
    currentChannel?.addReaction?.(message, key)
      .then((res) => {
        logger.info('Thread | useToggleReactionsCallback: Add reaction succeeded.', res);
      })
      .catch((err) => {
        logger.warning('Thread | useToggleReactionsCallback: Add reaction failed.', err);
      });
  }, [currentChannel]);
}
import { useCallback } from 'react';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { UserMessageCreateParams } from '@sendbird/chat/message';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';
import topics from '../../../../lib/pubSub/topics';

interface DynamicProps {
  isMentionEnabled: boolean;
  currentChannel: GroupChannel;
}
interface StaticProps {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useSendUserMessageCallback({
  isMentionEnabled,
  currentChannel,
}: DynamicProps, {
  logger,
  pubSub,
  threadDispatcher,
}: StaticProps): (props) => void {
  const sendMessage = useCallback((props) => {
    const {
      message,
      quoteMessage = null,
      mentionTemplate,
      mentionedUsers,
    } = props;
    const createDefaultParams = () => {
      const params = {} as UserMessageCreateParams;
      params.message = message;
      if (isMentionEnabled && mentionedUsers?.length > 0) {
        params.mentionedUsers = mentionedUsers;
      }
      if (isMentionEnabled && mentionTemplate && mentionedUsers?.length > 0) {
        params.mentionedMessageTemplate = mentionTemplate;
      }
      if (quoteMessage) {
        params.isReplyToChannel = true;
        params.parentMessageId = quoteMessage.messageId;
      }
      return params;
    };

    const params = createDefaultParams();
    logger.info('Thread | useSendUserMessageCallback: Sending user message start.', params);

    if (currentChannel?.sendUserMessage) {
      currentChannel?.sendUserMessage(params)
        .onPending((pendingMessage) => {
          threadDispatcher({
            type: ThreadContextActionTypes.SEND_MESSAGE_START,
            payload: { message: pendingMessage },
          });
        })
        .onFailed((error, message) => {
          logger.info('Thread | useSendUserMessageCallback: Sending user message failed.', { message, error });
          threadDispatcher({
            type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
            payload: { error, message },
          });
        })
        .onSucceeded((message) => {
          logger.info('Thread | useSendUserMessageCallback: Sending user message succeeded.', message);
          threadDispatcher({
            type: ThreadContextActionTypes.SEND_MESSAGE_SUCESS,
            payload: { message },
          });
          // because Thread doesn't subscribe SEND_USER_MESSAGE
          pubSub.publish(topics.SEND_USER_MESSAGE, {
            channel: currentChannel,
            message: message,
          });
        });
    }
  }, [isMentionEnabled, currentChannel]);
  return sendMessage;
}
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, MessageType, SendingStatus, UserMessage } from '@sendbird/chat/message';
import { useCallback } from 'react';
import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';
import topics from '../../../../lib/pubSub/topics';

interface DynamicProps {
  currentChannel: GroupChannel;
}
interface StaticProps {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}

export default function useResendMessageCallback({
  currentChannel,
}: DynamicProps, {
  logger,
  pubSub,
  threadDispatcher,
}: StaticProps): (failedMessage: UserMessage | FileMessage) => void {
  return useCallback((failedMessage: UserMessage | FileMessage) => {
    if ((failedMessage as UserMessage | FileMessage)?.isResendable) {
      failedMessage.sendingStatus = SendingStatus.PENDING;
      logger.info('Thread | useResendMessageCallback: Resending failedMessage start.', failedMessage);
      threadDispatcher({
        type: ThreadContextActionTypes.RESEND_MESSAGE_START,
        payload: failedMessage,
      });

      if (failedMessage?.isUserMessage?.() || failedMessage?.messageType === MessageType.USER) {
        currentChannel?.resendUserMessage(failedMessage as UserMessage)
          .then((message) => {
            logger.info('Thread | useResendMessageCallback: Resending failedMessage succeeded.', message);
            threadDispatcher({
              type: ThreadContextActionTypes.SEND_MESSAGE_SUCESS,
              payload: { message },
            });
            pubSub.publish(topics.SEND_USER_MESSAGE, {
              channel: currentChannel,
              message: message,
            });
          })
          .catch((error) => {
            logger.warning('Thread | useResendMessageCallback: Resending failedMessage failed.', error);
            failedMessage.sendingStatus = SendingStatus.FAILED;
            threadDispatcher({
              type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
              payload: { message: failedMessage },
            });
          });
      } else if (failedMessage?.isFileMessage?.() || failedMessage?.messageType === MessageType.FILE) {
        currentChannel?.resendFileMessage?.(failedMessage as FileMessage)
          .then((message) => {
            logger.info('Thread | useResendMessageCallback: Resending failedMessage succeeded.', message);
            threadDispatcher({
              type: ThreadContextActionTypes.SEND_MESSAGE_SUCESS,
              payload: { message },
            });
          })
          .catch((error) => {
            logger.warning('Thread | useResendMessageCallback: Resending failedMessage failed.', error);
            failedMessage.sendingStatus = SendingStatus.FAILED;
            threadDispatcher({
              type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
              payload: { message: failedMessage },
            });
            pubSub.publish(topics.SEND_FILE_MESSAGE, {
              channel: currentChannel,
              message: failedMessage,
            });
          });
      } else {
        logger.warning('Thread | useResendMessageCallback: Message is not resendable.', failedMessage);
        failedMessage.sendingStatus = SendingStatus.FAILED;
        threadDispatcher({
          type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
          payload: { message: failedMessage },
        });
      }
    }
  }, [currentChannel]);
}
import { useCallback } from 'react';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { FileMessage, FileMessageCreateParams, MessageMetaArray, UserMessage } from '@sendbird/chat/message';
import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import { ThreadContextActionTypes } from '../dux/actionTypes';
import topics from '../../../../lib/pubSub/topics';
import { scrollIntoLast } from '../utils';
import {
  META_ARRAY_MESSAGE_TYPE_KEY,
  META_ARRAY_MESSAGE_TYPE_VALUE__VOICE,
  META_ARRAY_VOICE_DURATION_KEY,
  VOICE_MESSAGE_FILE_NAME,
  VOICE_MESSAGE_MIME_TYPE,
} from '../../../../utils/consts';

interface DynamicParams {
  currentChannel: GroupChannel;
  onBeforeSendVoiceMessage?: (file: File, quoteMessage?: UserMessage | FileMessage) => FileMessageCreateParams;
}
interface StaticParams {
  logger: Logger;
  pubSub: any;
  threadDispatcher: CustomUseReducerDispatcher;
}
type FuncType = (file: File, duration: number, quoteMessage: UserMessage | FileMessage) => void;
interface LocalFileMessage extends FileMessage {
  localUrl: string;
  file: File;
}

export const useSendVoiceMessageCallback = ({
  currentChannel,
  onBeforeSendVoiceMessage,
}: DynamicParams,
{
  logger,
  pubSub,
  threadDispatcher,
}: StaticParams): FuncType => {
  const sendMessage = useCallback((file: File, duration: number, quoteMessage: UserMessage | FileMessage) => {
    const messageParams: FileMessageCreateParams = (
      onBeforeSendVoiceMessage
      && typeof onBeforeSendVoiceMessage === 'function'
    )
      ? onBeforeSendVoiceMessage(file, quoteMessage)
      : {
        file,
        fileName: VOICE_MESSAGE_FILE_NAME,
        mimeType: VOICE_MESSAGE_MIME_TYPE,
        metaArrays: [
          new MessageMetaArray({
            key: META_ARRAY_VOICE_DURATION_KEY,
            value: [`${duration}`],
          }),
          new MessageMetaArray({
            key: META_ARRAY_MESSAGE_TYPE_KEY,
            value: [META_ARRAY_MESSAGE_TYPE_VALUE__VOICE],
          }),
        ],
      };
    if (quoteMessage) {
      messageParams.isReplyToChannel = true;
      messageParams.parentMessageId = quoteMessage.messageId;
    }
    logger.info('Thread | useSendVoiceMessageCallback:  Start sending voice message', messageParams);
    currentChannel?.sendFileMessage(messageParams)
      .onPending((pendingMessage) => {
        threadDispatcher({
          type: ThreadContextActionTypes.SEND_MESSAGE_START,
          payload: {
            /* pubSub is used instead of messagesDispatcher
            to avoid redundantly calling `messageActionTypes.SEND_MESSAGEGE_START` */
            message: {
              ...pendingMessage,
              url: URL.createObjectURL(file),
              // pending thumbnail message seems to be failed
              requestState: 'pending',
            },
          },
        });
        setTimeout(() => scrollIntoLast(), 1000);
      })
      .onFailed((error, message) => {
        (message as LocalFileMessage).localUrl = URL.createObjectURL(file);
        (message as LocalFileMessage).file = file;
        logger.info('Thread | useSendVoiceMessageCallback: Sending voice message failed.', { message, error });
        threadDispatcher({
          type: ThreadContextActionTypes.SEND_MESSAGE_FAILURE,
          payload: { message, error },
        });
      })
      .onSucceeded((message) => {
        logger.info('Thread | useSendVoiceMessageCallback: Sending voice message succeeded.', message);
        pubSub.publish(topics.SEND_FILE_MESSAGE, {
          channel: currentChannel,
          message: message,
        });
      });
  }, [
    currentChannel,
    onBeforeSendVoiceMessage,
  ]);
  return sendMessage;
};

export default useSendVoiceMessageCallback;
import React, { useReducer, useMemo, useEffect, ReactElement } from 'react';
import { User } from '@sendbird/chat';
import { GroupChannel } from '@sendbird/chat/groupChannel';
import { BaseMessage, FileMessage, FileMessageCreateParams, UserMessage } from '@sendbird/chat/message';

import { getNicknamesMapFromMembers, getParentMessageFrom } from './utils';
import { UserProfileProvider } from '../../../lib/UserProfileContext';
import { CustomUseReducerDispatcher } from '../../../lib/SendbirdState';
import useSendbirdStateContext from '../../../hooks/useSendbirdStateContext';

import threadReducer from './dux/reducer';
import { ThreadContextActionTypes } from './dux/actionTypes';
import threadInitialState, { ThreadContextInitialState } from './dux/initialState';

import useGetChannel from './hooks/useGetChannel';
import useGetAllEmoji from './hooks/useGetAllEmoji';
import useGetThreadList from './hooks/useGetThreadList';
import useGetParentMessage from './hooks/useGetParentMessage';
import useHandlePubsubEvents from './hooks/useHandlePubsubEvents';
import useHandleChannelEvents from './hooks/useHandleChannelEvents';
import useSendFileMessageCallback from './hooks/useSendFileMessage';
import useUpdateMessageCallback from './hooks/useUpdateMessageCallback';
import useDeleteMessageCallback from './hooks/useDeleteMessageCallback';
import useGetPrevThreadsCallback from './hooks/useGetPrevThreadsCallback';
import useGetNextThreadsCallback from './hooks/useGetNextThreadsCallback';
import useToggleReactionCallback from './hooks/useToggleReactionsCallback';
import useSendUserMessageCallback from './hooks/useSendUserMessageCallback';
import useResendMessageCallback from './hooks/useResendMessageCallback';
import useSendVoiceMessageCallback from './hooks/useSendVoiceMessageCallback';

export type ThreadProviderProps = {
  children?: React.ReactElement;
  channelUrl: string;
  message: UserMessage | FileMessage | null;
  onHeaderActionClick?: () => void;
  onMoveToParentMessage?: (props: { message: UserMessage | FileMessage, channel: GroupChannel }) => void;
  onBeforeSendVoiceMessage?: (file: File, quotedMessage?: UserMessage | FileMessage) => FileMessageCreateParams;
  // User Profile
  disableUserProfile?: boolean;
  renderUserProfile?: (props: { user: User, close: () => void }) => ReactElement;
  onUserProfileMessage?: (channel: GroupChannel) => void;
};
export interface ThreadProviderInterface extends ThreadProviderProps, ThreadContextInitialState {
  // hooks for fetching threads
  fetchPrevThreads: (callback?: (messages?: Array<BaseMessage>) => void) => void;
  fetchNextThreads: (callback?: (messages?: Array<BaseMessage>) => void) => void;
  toggleReaction: (message, key, isReacted) => void;
  sendMessage: (props: {
    message: UserMessage,
    quoteMessage?: UserMessage | FileMessage,
    mentionTemplate?: string,
    mentionedUsers?: Array<User>,
  }) => void;
  sendFileMessage: (file: File, quoteMessage: UserMessage | FileMessage) => void;
  sendVoiceMessage: (file: File, duration: number, quoteMessage?: UserMessage | FileMessage) => void;
  resendMessage: (failedMessage: UserMessage | FileMessage) => void;
  updateMessage: (props, callback?: () => void) => void;
  deleteMessage: (message: UserMessage | FileMessage) => Promise<UserMessage | FileMessage>;
  nicknamesMap: Map<string, string>;
}
const ThreadContext = React.createContext<ThreadProviderInterface | null>(null);

export const ThreadProvider: React.FC<ThreadProviderProps> = (props: ThreadProviderProps) => {
  const {
    children,
    channelUrl,
    onHeaderActionClick,
    onMoveToParentMessage,
    onBeforeSendVoiceMessage,
    // User Profile
    disableUserProfile,
    renderUserProfile,
    onUserProfileMessage,
  } = props;
  const propsMessage = props?.message;
  const propsParentMessage = getParentMessageFrom(propsMessage);
  // Context from SendbirdProvider
  const globalStore = useSendbirdStateContext();
  const { stores, config } = globalStore;
  // // stores
  const { sdkStore, userStore } = stores;
  const { sdk } = sdkStore;
  const { user } = userStore;
  const sdkInit = sdkStore?.initialized;
  // // config
  const {
    logger,
    pubSub,
    replyType,
    isMentionEnabled,
    isReactionEnabled,
  } = config;

  // dux of Thread
  const [threadStore, threadDispatcher] = useReducer(
    threadReducer,
    threadInitialState,
  ) as [ThreadContextInitialState, CustomUseReducerDispatcher];
  const {
    currentChannel,
    allThreadMessages,
    parentMessage,
    channelState,
    threadListState,
    parentMessageState,
    hasMorePrev,
    hasMoreNext,
    emojiContainer,
    isMuted,
    isChannelFrozen,
    currentUserId,
  }: ThreadContextInitialState = threadStore;

  // Initialization
  useEffect(() => {
    threadDispatcher({
      type: ThreadContextActionTypes.INIT_USER_ID,
      payload: user?.userId,
    });
  }, [user]);
  useGetChannel({
    channelUrl,
    sdkInit,
    message: propsMessage,
  }, { sdk, logger, threadDispatcher });
  useGetParentMessage({
    channelUrl,
    sdkInit,
    parentMessage: propsParentMessage,
  }, { sdk, logger, threadDispatcher });
  useGetThreadList({
    sdkInit,
    parentMessage,
    isReactionEnabled,
    anchorMessage: propsMessage?.messageId !== propsParentMessage?.messageId ? propsMessage : null,
    // anchorMessage should be null when parentMessage doesn't exist
  }, { logger, threadDispatcher });
  useGetAllEmoji({ sdk }, { logger, threadDispatcher });
  // Handle channel events
  useHandleChannelEvents({
    sdk,
    currentChannel,
  }, { logger, threadDispatcher });
  useHandlePubsubEvents({
    sdkInit,
    currentChannel,
    parentMessage,
  }, { logger, pubSub, threadDispatcher });

  // callbacks
  const fetchPrevThreads = useGetPrevThreadsCallback({
    hasMorePrev,
    parentMessage,
    threadListState,
    isReactionEnabled,
    oldestMessageTimeStamp: allThreadMessages[0]?.createdAt || 0,
  }, { logger, threadDispatcher });
  const fetchNextThreads = useGetNextThreadsCallback({
    hasMoreNext,
    parentMessage,
    threadListState,
    isReactionEnabled,
    latestMessageTimeStamp: allThreadMessages[allThreadMessages.length - 1]?.createdAt || 0,
  }, { logger, threadDispatcher });
  const toggleReaction = useToggleReactionCallback({ currentChannel }, { logger });
  const sendMessage = useSendUserMessageCallback({
    isMentionEnabled,
    currentChannel,
  }, { logger, pubSub, threadDispatcher });
  const sendFileMessage = useSendFileMessageCallback({
    currentChannel,
  }, { logger, pubSub, threadDispatcher });
  const sendVoiceMessage = useSendVoiceMessageCallback({
    currentChannel, onBeforeSendVoiceMessage,
  }, { logger, pubSub, threadDispatcher });
  const resendMessage = useResendMessageCallback({
    currentChannel,
  }, { logger, pubSub, threadDispatcher });
  const updateMessage = useUpdateMessageCallback({
    currentChannel,
    isMentionEnabled,
  }, { logger, pubSub, threadDispatcher });
  const deleteMessage = useDeleteMessageCallback({ currentChannel, threadDispatcher }, { logger });

  // memo
  const nicknamesMap: Map<string, string> = useMemo(() => (
    (replyType && currentChannel)
      ? getNicknamesMapFromMembers(currentChannel?.members)
      : new Map()
  ), [currentChannel?.members]);

  return (
    <ThreadContext.Provider
      value={{
        // ThreadProviderProps
        channelUrl,
        message: propsMessage,
        onHeaderActionClick,
        onMoveToParentMessage,
        // ThreadContextInitialState
        currentChannel,
        allThreadMessages,
        parentMessage,
        channelState,
        threadListState,
        parentMessageState,
        hasMorePrev,
        hasMoreNext,
        emojiContainer,
        // hooks
        fetchPrevThreads,
        fetchNextThreads,
        toggleReaction,
        sendMessage,
        sendFileMessage,
        sendVoiceMessage,
        resendMessage,
        updateMessage,
        deleteMessage,
        // context
        nicknamesMap,
        isMuted,
        isChannelFrozen,
        currentUserId,
      }}
    >
      {/* UserProfileProvider */}
      <UserProfileProvider
        disableUserProfile={disableUserProfile}
        renderUserProfile={renderUserProfile}
        onUserProfileMessage={onUserProfileMessage}
      >
        {children}
      </UserProfileProvider>
    </ThreadContext.Provider>
  );
};

export type UseThreadContextType = () => ThreadProviderInterface;
export const useThreadContext: UseThreadContextType = () => React.useContext(ThreadContext);
