export enum ButtonTypes {
  PRIMARY = 'PRIMARY',
  SECONDARY = 'SECONDARY',
  DANGER = 'DANGER',
  DISABLED = 'DISABLED',
}
export enum ButtonSizes {
  BIG = 'BIG',
  SMALL = 'SMALL',
}
import { ButtonTypes, ButtonSizes } from './types';

export function changeTypeToClassName(type: ButtonTypes): string {
  switch (type) {
    case ButtonTypes.PRIMARY: return 'sendbird-button--primary';
    case ButtonTypes.SECONDARY: return 'sendbird-button--secondary';
    case ButtonTypes.DANGER: return 'sendbird-button--danger';
    case ButtonTypes.DISABLED: return 'sendbird-button--disabled';
    default: return null;
  }
}

export function changeSizeToClassName(size: ButtonSizes): string {
  switch (size) {
    case ButtonSizes.BIG: return 'sendbird-button--big';
    case ButtonSizes.SMALL: return 'sendbird-button--small';
    default: return null;
  }
}
import React, { ReactElement } from 'react';

import './index.scss';
import Label, { LabelTypography, LabelColors } from '../Label';
import { changeTypeToClassName, changeSizeToClassName } from './utils';
import {
  ButtonTypes as ButtonTypes_,
  ButtonSizes as ButtonSizes_,
} from './types';

export const ButtonTypes = ButtonTypes_;
export const ButtonSizes = ButtonSizes_;

export interface ButtonProps {
  className?: string | Array<string>;
  type?: ButtonTypes_;
  size?: ButtonSizes_;
  children: string | ReactElement;
  disabled?: boolean;
  onClick?: () => void;
}

export default function Button({
  className,
  type = ButtonTypes.PRIMARY,
  size = ButtonSizes.BIG,
  children = 'Button',
  disabled = false,
  onClick = () => { /* noop */ },
}: ButtonProps): ReactElement {
  const injectingClassNames = [
    ...((Array.isArray(className)) ? className : [className]),
    'sendbird-button',
    (disabled ? 'sendbird-button__disabled' : ''),
    changeTypeToClassName(type),
    changeSizeToClassName(size),
  ].join(' ');

  return (
    <button
      className={injectingClassNames}
      type="button"
      onClick={onClick}
      disabled={disabled}
    >
      <Label
        className="sendbird-button__text"
        type={LabelTypography.BUTTON_1}
        color={LabelColors.ONCONTENT_1}
      >
        {children}
      </Label>
    </button>
  );
}
