import React, { ReactElement, useState } from 'react';
import { EmojiContainer } from '@sendbird/chat';
import { GroupChannel, Member } from '@sendbird/chat/groupChannel';
import { FileMessage, Reaction, UserMessage } from '@sendbird/chat/message';

import './mobile-menu-reacted-members.scss';

import BottomSheet from '../BottomSheet';
import { getEmojiUrl } from '../../utils';
import ImageRenderer from '../ImageRenderer';
import Icon, { IconColors, IconTypes } from '../Icon';
import Label, { LabelColors, LabelTypography } from '../Label';
import UserListItem from '../UserListItem';

export interface ReactedMembersBottomSheetProps {
  message: UserMessage | FileMessage;
  channel: GroupChannel;
  emojiKey: string;
  hideMenu: () => void;
  emojiContainer?: EmojiContainer;
}

export const ReactedMembersBottomSheet = ({
  message,
  channel,
  emojiKey = '',
  hideMenu,
  emojiContainer,
}: ReactedMembersBottomSheetProps): ReactElement => {
  const { members = [] } = channel;
  const [selectedEmoji, setSelectedEmoji] = useState(emojiKey);

  return (
    <BottomSheet onBackdropClick={hideMenu}>
      <div className="sendbird-message__bottomsheet">
        <div className="sendbird-message__bottomsheet__reacted-members">
          {message.reactions?.map((reaction: Reaction): ReactElement => {
            const emojiUrl = getEmojiUrl(emojiContainer, reaction.key);
            return (
              <div
                key={reaction.key}
                className={`
                  sendbird-message__bottomsheet__reacted-members__item
                  ${selectedEmoji === reaction.key ? 'sendbird-message__bottomsheet__reacted-members__item__selected' : ''}
                `}
                onClick={() => {
                  setSelectedEmoji(reaction.key);
                }}
              >
                <ImageRenderer
                  url={emojiUrl}
                  width="28px"
                  height="28px"
                  placeHolder={(style) => (
                    <div style={style}>
                      <Icon
                        type={IconTypes.QUESTION}
                        fillColor={IconColors.ON_BACKGROUND_3}
                        width="28px"
                        height="28px"
                      />
                    </div>
                  )}
                />
                <Label
                  type={LabelTypography.BUTTON_2}
                  color={selectedEmoji === reaction.key ? LabelColors.PRIMARY : LabelColors.ONBACKGROUND_3}
                >
                  {reaction.userIds.length}
                </Label>
              </div>
            );
          })}
        </div>
        <div className="sendbird-message__bottomsheet__reactor-list">
          { // making a member list who reacted to the message with the `selectedEmoji`
            (
              message.reactions?.find(reaction => reaction.key === selectedEmoji)
                ?.userIds.map((userId) => members.find((member) => member.userId === userId))
                .filter((member) => member !== undefined) as Array<Member>
            )
              .map((member) => (
                <UserListItem
                  key={member.userId}
                  className="sendbird-message__bottomsheet__reactor-list__item"
                  user={member}
                  avatarSize="36px"
                />
              ))
          }
        </div>
      </div>
    </BottomSheet>
  );
};
import React, { useContext } from 'react';

import { FileMessage, Reaction, UserMessage } from '@sendbird/chat/message';

import Tooltip from '../Tooltip';
import TooltipWrapper from '../TooltipWrapper';
import ReactionBadge from '../ReactionBadge';
import ImageRenderer from '../ImageRenderer';
import Icon, { IconTypes } from '../Icon';
import { useMediaQueryContext } from '../../lib/MediaQueryContext';
import useLongPress from '../../hooks/useLongPress';
import { LocalizationContext } from '../../lib/LocalizationContext';
import useSendbirdStateContext from '../../hooks/useSendbirdStateContext';
import { getEmojiTooltipString, isReactedBy } from '../../utils';
import { useMessageContext } from '../../modules/Message/context/MessageProvider';
import { Emoji } from '@sendbird/chat';

type Props = {
  reaction: Reaction;
  memberNicknamesMap: Map<string, string>;
  setEmojiKey: React.Dispatch<React.SetStateAction<string>>;
  toggleReaction?: (message: UserMessage | FileMessage, key: string, byMe: boolean) => void;
  emojisMap: Map<string, Emoji>;
};

export default function ReactionItem({
  reaction,
  memberNicknamesMap,
  setEmojiKey,
  toggleReaction,
  emojisMap,
}: Props) {
  const store = useSendbirdStateContext();
  const { isMobile } = useMediaQueryContext();
  const messageStore = useMessageContext();
  const message = messageStore?.message as UserMessage;
  const { stringSet } = useContext(LocalizationContext);

  const userId = store.config.userId;
  const reactedByMe = isReactedBy(userId, reaction);

  const handleOnClick = () => {
    setEmojiKey('');
    toggleReaction?.((message), reaction.key, reactedByMe);
  };
  const longPress = useLongPress({
    onLongPress: () => {
      setEmojiKey(reaction.key);
    },
    onClick: handleOnClick,
  }, {
    shouldPreventDefault: true,
    shouldStopPropagation: true,
  });

  return (
    <TooltipWrapper
      className="sendbird-emoji-reactions__reaction-badge"
      hoverTooltip={(reaction.userIds.length > 0) ? (
        <Tooltip>
          {getEmojiTooltipString(reaction, userId, memberNicknamesMap, stringSet)}
        </Tooltip>
      ) : <></>}
    >
      <div
        {...(
          isMobile
            ? longPress
            : { onClick: handleOnClick }
        )}
      >
        <ReactionBadge
          count={reaction.userIds.length}
          selected={reactedByMe}
        >
          <ImageRenderer
            circle
            url={emojisMap.get(reaction?.key)?.url || ''}
            width="20px"
            height="20px"
            defaultComponent={(
              <Icon width="20px" height="20px" type={IconTypes.QUESTION} />
            )}
          />
        </ReactionBadge>
      </div>
    </TooltipWrapper>
  );
}
import React, {
  KeyboardEvent,
  MouseEvent,
  TouchEvent,
} from 'react';
import ReactionBadge from '../ReactionBadge';
import Icon, { IconColors, IconTypes } from '../Icon';
import useLongPress from '../../hooks/useLongPress';

export interface AddReactionBadgeItemProps {
  onClick: (e: MouseEvent<HTMLDivElement> | KeyboardEvent<HTMLDivElement> | TouchEvent<HTMLDivElement>) => void;
}

export const AddReactionBadgeItem = ({
  onClick,
}: AddReactionBadgeItemProps): React.ReactElement => {
  const onlyClick = useLongPress({
    onLongPress: () => { /* noop */ },
    onClick,
  }, {
    shouldPreventDefault: true,
    shouldStopPropagation: true,
  });

  return (
    <div
      className="sendbird-emoji-reactions__add-reaction-badge"
      {...onlyClick}
    >
      <ReactionBadge
        isAdd
      >
        <Icon
          type={IconTypes.EMOJI_MORE}
          fillColor={IconColors.ON_BACKGROUND_3}
          width="20px"
          height="20px"
        />
      </ReactionBadge>
    </div>
  );
};
import React, { ReactElement, useMemo } from 'react';
import { EmojiContainer } from '@sendbird/chat';
import { FileMessage, Reaction, UserMessage } from '@sendbird/chat/message';

import BottomSheet from '../BottomSheet';
import { getEmojiListAll } from '../../utils';
import ReactionButton from '../ReactionButton';
import ImageRenderer from '../ImageRenderer';
import Icon, { IconColors, IconTypes } from '../Icon';

export interface MobileEmojisBottomSheetProps {
  userId: string;
  message: UserMessage | FileMessage;
  emojiContainer: EmojiContainer;
  hideMenu: () => void;
  toggleReaction?: (message: UserMessage | FileMessage, key: string, byMe: boolean) => void;
}

export const MobileEmojisBottomSheet = ({
  userId,
  message,
  emojiContainer,
  hideMenu,
  toggleReaction,
}: MobileEmojisBottomSheetProps): ReactElement => {
  const emojiAllList = useMemo(() => {
    return getEmojiListAll(emojiContainer);
  }, [emojiContainer]);
  return (
    <BottomSheet onBackdropClick={hideMenu}>
      <div className="sendbird-message__bottomsheet sendbird-message__emojis-bottomsheet">
        {emojiAllList.map((emoji) => {
          const isReacted: boolean = (message?.reactions
            ?.find((reaction: Reaction): boolean => reaction.key === emoji.key)?.userIds
            ?.some((reactorId: string): boolean => reactorId === userId)) ?? false;
          return (
            <ReactionButton
              key={emoji.key}
              width="44px"
              height="44px"
              selected={isReacted}
              onClick={(e) => {
                e?.stopPropagation();
                toggleReaction?.(message, emoji.key, isReacted);
                hideMenu();
              }}
              dataSbId={`ui_mobile_emoji_reactions_menu_${emoji.key}`}
            >
              <ImageRenderer
                url={emoji.url}
                width="38px"
                height="38px"
                placeHolder={(style: Record<string, unknown>): ReactElement => (
                  <div style={style}>
                    <Icon
                      type={IconTypes.QUESTION}
                      fillColor={IconColors.ON_BACKGROUND_3}
                      width="28px"
                      height="28px"
                    />
                  </div>
                )}
              />
            </ReactionButton>
          );
        })}
      </div>
    </BottomSheet>
  );
};
import './index.scss';
import React, { ReactElement, useRef, useState } from 'react';
import type { Emoji, EmojiContainer } from '@sendbird/chat';
import type { FileMessage, Reaction, UserMessage } from '@sendbird/chat/message';
import type { GroupChannel } from '@sendbird/chat/groupChannel';

import ReactionBadge from '../ReactionBadge';
import ReactionButton from '../ReactionButton';
import ImageRenderer from '../ImageRenderer';
import Icon, { IconTypes, IconColors } from '../Icon';
import ContextMenu, { EmojiListItems } from '../ContextMenu';
import { Nullable, SpaceFromTriggerType } from '../../types';

import { getClassName, getEmojiListAll, getEmojiMapAll } from '../../utils';
import { ReactedMembersBottomSheet } from '../MobileMenu/ReactedMembersBottomSheet';
import ReactionItem from './ReactionItem';
import { useMediaQueryContext } from '../../lib/MediaQueryContext';
import { AddReactionBadgeItem } from './AddReactionBadgeItem';
import { MobileEmojisBottomSheet } from '../MobileMenu/MobileEmojisBottomSheet';

interface Props {
  className?: string | Array<string>;
  userId: string;
  message: UserMessage | FileMessage;
  channel: Nullable<GroupChannel>;
  emojiContainer: EmojiContainer;
  memberNicknamesMap: Map<string, string>;
  spaceFromTrigger?: SpaceFromTriggerType;
  isByMe?: boolean;
  toggleReaction?: (message: UserMessage | FileMessage, key: string, byMe: boolean) => void;
}

const EmojiReactions = ({
  className = '',
  userId,
  message,
  channel,
  emojiContainer,
  memberNicknamesMap,
  spaceFromTrigger = { x: 0, y: 0 },
  isByMe = false,
  toggleReaction,
}: Props): ReactElement => {
  const { isMobile } = useMediaQueryContext();
  const addReactionRef = useRef(null);
  const [showEmojiList, setShowEmojiList] = useState(false);
  const [selectedEmojiKey, setSelectedEmojiKey] = useState('');

  const emojisMap = getEmojiMapAll(emojiContainer);
  const showAddReactionBadge = (message.reactions?.length ?? 0) < emojisMap.size;

  return (
    <div className={getClassName([
      className, 'sendbird-emoji-reactions',
      isByMe ? 'outgoing' : 'incoming',
    ])}>
      {((message.reactions?.length ?? 0) > 0) && (
        message.reactions?.map((reaction: Reaction): ReactElement => {
          return (
            <ReactionItem
              key={reaction?.key}
              reaction={reaction}
              memberNicknamesMap={memberNicknamesMap}
              setEmojiKey={setSelectedEmojiKey}
              toggleReaction={toggleReaction}
              emojisMap={emojisMap}
            />
          );
        })
      )}
      {(!isMobile && showAddReactionBadge) && (
        <ContextMenu
          menuTrigger={(toggleDropdown: () => void): ReactElement => (
            <ReactionBadge
              className="sendbird-emoji-reactions__add-reaction-badge"
              ref={addReactionRef}
              isAdd
              onClick={(e) => {
                toggleDropdown();
                e?.stopPropagation?.();
              }}
            >
              <Icon
                type={IconTypes.EMOJI_MORE}
                fillColor={IconColors.ON_BACKGROUND_3}
                width="20px"
                height="20px"
              />
            </ReactionBadge>
          )}
          menuItems={(closeDropdown: () => void): ReactElement => (
            <EmojiListItems
              parentRef={addReactionRef}
              parentContainRef={addReactionRef}
              closeDropdown={closeDropdown}
              spaceFromTrigger={spaceFromTrigger}
            >
              {getEmojiListAll(emojiContainer).map((emoji: Emoji): ReactElement => {
                const isReacted: boolean = (message?.reactions
                  ?.find((reaction: Reaction): boolean => reaction.key === emoji.key)?.userIds
                  ?.some((reactorId: string): boolean => reactorId === userId)) || false;
                return (
                  <ReactionButton
                    key={emoji.key}
                    width="36px"
                    height="36px"
                    selected={isReacted}
                    onClick={(e): void => {
                      closeDropdown();
                      toggleReaction?.(message, emoji.key, isReacted);
                      e?.stopPropagation();
                    }}
                    dataSbId={`ui_emoji_reactions_menu_${emoji.key}`}
                  >
                    <ImageRenderer
                      url={emoji?.url || ''}
                      width="28px"
                      height="28px"
                      placeHolder={(style: Record<string, unknown>): ReactElement => (
                        <div style={style}>
                          <Icon
                            type={IconTypes.QUESTION}
                            fillColor={IconColors.ON_BACKGROUND_3}
                            width="28px"
                            height="28px"
                          />
                        </div>
                      )}
                    />
                  </ReactionButton>
                );
              })}
            </EmojiListItems>
          )}
        />
      )}
      {(isMobile && showAddReactionBadge) && (
        <AddReactionBadgeItem
          onClick={() => {
            setShowEmojiList(true);
          }}
        />
      )}
      {(isMobile && showEmojiList) && (
        <MobileEmojisBottomSheet
          userId={userId}
          message={message}
          emojiContainer={emojiContainer}
          hideMenu={() => {
            setShowEmojiList(false);
          }}
          toggleReaction={toggleReaction}
        />
      )}
      {(isMobile && selectedEmojiKey && channel !== null) && (
        <ReactedMembersBottomSheet
          message={message}
          channel={channel}
          emojiKey={selectedEmojiKey}
          hideMenu={() => {
            setSelectedEmojiKey('');
          }}
          emojiContainer={emojiContainer}
        />
      )}
    </div>
  );
};

export default EmojiReactions;
